import { useEffect, useState } from "react";

const BaseTable = () => {
  const [data, setData] = useState([]);
  const [minValue, setMinValue] = useState(0);
  const [maxValue, setMaxValue] = useState(100);

  const cities = ["New York", "London", "Paris", "Tokyo", "Sydney", "Moscow", "Berlin", "Rome", "Toronto", "Beijing"];
  const countries = ["USA", "UK", "France", "Japan", "Australia", "Russia", "Germany", "Italy", "Canada", "China"];

  const generateRandomData = () => {
    let data = [];
    for (let i = 0; i < 20; i++) {
      data.push({
        name: `Person${i + 1}`,
        city: cities[Math.floor(Math.random() * cities.length)],
        country: countries[Math.floor(Math.random() * countries.length)],
        "income-amount": Math.floor(Math.random() * 1000000),
        "total-revenue": Math.floor(Math.random() * 1000000),
      });
    }
    return data;
  };

  useEffect(() => {
    fetch("https://api.example.com/data")
      .then((response) => response.json())
      .then((data) => {
        // The API you mentioned requires server-side integration,
        // so we'll just use some dummy data for this example
        const dummyData = data.map((item, index) => ({
          name: `Charity${index + 1}`,
          city: "Dummy city",
          country: "Dummy country",
          incomeAmount: Math.floor(Math.random() * 100),
          totalRevenue: Math.floor(Math.random() * 100),
        }));
        setData(dummyData);
        const maxIncome = Math.max(...dummyData.map((item) => item.incomeAmount));
        setMaxValue(maxIncome);
      });
  }, []);

  const filteredData = data.filter((item) => item.incomeAmount >= minValue && item.incomeAmount <= maxValue);

  return (
    <div>
      <label>
        Income range:
        <input
          type="range"
          min="0"
          max={maxValue}
          value={minValue}
          onChange={(event) => setMinValue(Number(event.target.value))}
        />
        <input
          type="range"
          min={minValue}
          max={maxValue}
          value={maxValue}
          onChange={(event) => setMaxValue(Number(event.target.value))}
        />
      </label>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>City</th>
            <th>Country</th>
            <th>Income Amount</th>
            <th>Total Revenue</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.city}</td>
              <td>{item.country}</td>
              <td>{item.incomeAmount}</td>
              <td>{item.totalRevenue}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BaseTable;
