import { useCallback, useEffect, useState } from "react";

const cities = ["New York", "London", "Paris", "Tokyo", "Sydney", "Moscow", "Berlin", "Rome", "Toronto", "Beijing"];
const countries = ["USA", "UK", "France", "Japan", "Australia", "Russia", "Germany", "Italy", "Canada", "China"];

const BaseTable = () => {
  const [data, setData] = useState([]);
  const [minValue, setMinValue] = useState(0);
  const [maxValue, setMaxValue] = useState(1000000);

  const generateRandomData = useCallback(() => {
    let arr = [];
    for (let i = 0; i < 20; i++) {
      arr.push({
        name: `Person${i + 1}`,
        city: cities[Math.floor(Math.random() * cities.length)],
        country: countries[Math.floor(Math.random() * countries.length)],
        incomeAmount: Math.floor(Math.random() * 1000000),
        totalRevenue: Math.floor(Math.random() * 1000000),
      });
    }
    return arr;
  }, []);

  useEffect(() => {
    const generetedData = generateRandomData();
    const filteredData = generetedData.filter((item) => item.incomeAmount >= minValue && item.incomeAmount <= maxValue);
    setData(filteredData);
    console.log("filteredData", filteredData);
  }, [generateRandomData, minValue, maxValue]);

  return (
    <div>
      <label>
        Income range:
        <input
          type="range"
          min="0"
          max={maxValue}
          value={minValue}
          onChange={(event) => setMinValue(Number(event.target.value))}
        />
        <input
          type="range"
          min={minValue}
          max={maxValue}
          value={maxValue}
          onChange={(event) => setMaxValue(Number(event.target.value))}
        />
      </label>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>City</th>
            <th>Country</th>
            <th>Income Amount</th>
            <th>Total Revenue</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.city}</td>
              <td>{item.country}</td>
              <td>{item.incomeAmount}</td>
              <td>{item.totalRevenue}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BaseTable;
